    PRINT_ERR_CODE(SQLITE_IOERR_DIR_FSYNC);
    PRINT_ERR_CODE(SQLITE_IOERR_TRUNCATE);
    PRINT_ERR_CODE(SQLITE_IOERR_FSTAT);
    PRINT_ERR_CODE(SQLITE_IOERR_UNLOCK);
    PRINT_ERR_CODE(SQLITE_IOERR_RDLOCK);
    PRINT_ERR_CODE(SQLITE_IOERR_DELETE);
    PRINT_ERR_CODE(SQLITE_IOERR_BLOCKED);
    PRINT_ERR_CODE(SQLITE_IOERR_NOMEM);
    PRINT_ERR_CODE(SQLITE_IOERR_ACCESS);
    PRINT_ERR_CODE(SQLITE_IOERR_CHECKRESERVEDLOCK);
    PRINT_ERR_CODE(SQLITE_IOERR_LOCK);
    PRINT_ERR_CODE(SQLITE_IOERR_CLOSE);
    PRINT_ERR_CODE(SQLITE_IOERR_DIR_CLOSE);
    PRINT_ERR_CODE(SQLITE_IOERR_SHMOPEN);
    PRINT_ERR_CODE(SQLITE_IOERR_SHMSIZE);
    PRINT_ERR_CODE(SQLITE_IOERR_SHMLOCK);
    PRINT_ERR_CODE(SQLITE_IOERR_SHMMAP);
    PRINT_ERR_CODE(SQLITE_IOERR_SEEK);
    PRINT_ERR_CODE(SQLITE_IOERR_DELETE_NOENT);
    PRINT_ERR_CODE(SQLITE_IOERR_MMAP);
    PRINT_ERR_CODE(SQLITE_IOERR_GETTEMPPATH);
    PRINT_ERR_CODE(SQLITE_IOERR_CONVPATH);
    PRINT_ERR_CODE(SQLITE_LOCKED_SHAREDCACHE);
    PRINT_ERR_CODE(SQLITE_BUSY_RECOVERY);
    PRINT_ERR_CODE(SQLITE_BUSY_SNAPSHOT);
    PRINT_ERR_CODE(SQLITE_CANTOPEN_NOTEMPDIR);
    PRINT_ERR_CODE(SQLITE_CANTOPEN_ISDIR);
    PRINT_ERR_CODE(SQLITE_CANTOPEN_FULLPATH);
    PRINT_ERR_CODE(SQLITE_CANTOPEN_CONVPATH);
    PRINT_ERR_CODE(SQLITE_CORRUPT_VTAB);
    PRINT_ERR_CODE(SQLITE_READONLY_RECOVERY);
    PRINT_ERR_CODE(SQLITE_READONLY_CANTLOCK);
    PRINT_ERR_CODE(SQLITE_READONLY_ROLLBACK);
